#!/bin/bash

SCRIPT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )"
sudo cp $SCRIPT_DIR/RPM-GPG-KEY-MYSQL56 /etc/pki/rpm-gpg/RPM-GPG-KEY-MYSQL56
sudo mv $SCRIPT_DIR/mysql-community-5.6.repo /etc/yum.repos.d/
sudo yum -y install mysql-community-server
sudo systemctl enable mysqld
sudo systemctl start mysqld
sudo mysql -e "delete from mysql.user where user='';"
sudo mysql -e "GRANT ALL PRIVILEGES ON *.* TO '$1'@'localhost' IDENTIFIED BY '$2' with GRANT OPTION;"

